window.Echo.private("chat."+loggedInUserId).listen("ChatEvent",(e=>{if(console.log(e),e.senderId==$("#mychatbox").attr("data-inbox")){let t=`\n            <div class="chat-item chat-left" style=""><img style="width:50px;height:50px;object-fit:cover;" src="${e.avatar}"><div class="chat-details"><div class="chat-text">${e.message}</div><div class="chat-time">sending...</div></div></div>\n            `;$(".chat-content").append(t),function(){let e=$(".chat-content");e.scrollTop(e.prop("scrollHeight"))}()}$(".fp_chat_user").each((function(){let t=$(this).data("user");if(e.senderId==t){let e='<i class="beep"></i>new message';$(this).find(".got_new_message").html(e)}})),$(".message-envelope").addClass("beep")}));
